var title_f0_13_221="Ingredient label terms for tree nut";
var content_f0_13_221=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F56449&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F56449&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    How to read an ingredient label for a tree nut-free diet",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Avoid foods that contain nuts or any of these ingredients:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Almond",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Artificial nuts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Beechnut",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Brazil nut",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Butternut",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cashew",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chestnut",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chinquapin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Coconut",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Filbert/hazelnut",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Gianduja (a chocolate-nut mixture)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ginkgo nut",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hickory nut",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Litchi/lichee/lychee nut",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Macadamia nut",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Marzipan/almond paste",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nangai nut",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Natural nut extract (eg, almond, walnut)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nut butters (eg, cashew butter)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nut meal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nut meat",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nut paste (eg, almond paste)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nut pieces",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pecan",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pesto",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pili nut",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pine nut (also referred to as Indian, pignoli, pig&ntilde;olia, pignon, pi&ntilde;on, and pinyon nut)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pistachio",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Praline",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Shea nut",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Walnut",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Tree nuts are sometimes found in the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Black walnut hull extract (flavoring)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Natural nut extract",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nut distillates/alcoholic extracts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nut oils (eg, walnut oil, almond oil)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Walnut hull extract (flavoring)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Keep the following in mind:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Mortadella may contain pistachios.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; There is no evidence that coconut oil and shea nut oil/butter are allergenic.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Many experts advise patients allergic to tree nuts to avoid peanuts as well.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Talk to your doctor if you find other nuts not listed here.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    All FDA-regulated manufactured food products that contain a tree nut as an ingredient are required by US law to list the specific tree nut on the product label.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Copyright &copy; 2010 The Food Allergy &amp; Anaphylaxis Network. Used with permission.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
