" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pathophysiology of ischemic stroke",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?0/15/249/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?0/15/249/contributors\">",
"     Arshad Majid, MB, ChB, FRCP",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?0/15/249/contributors\">",
"     Daniel B Zemke, PhD",
"    </a>",
