" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Syndromes with craniofacial abnormalities",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?0/15/250/contributors\">",
