var title_f0_15_252="Clinical findings by stage of hypothermia";
var content_f0_15_252=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F62978&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F62978&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical findings by stage of hypothermia*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"20%\">",
"     </colgroup>",
"     <colgroup span=\"2\" width=\"40%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Stage",
"       </td>",
"       <td class=\"subtitle1\">",
"        Core temperature",
"       </td>",
"       <td class=\"subtitle1\">",
"        Possible findings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Mild",
"       </td>",
"       <td rowspan=\"3\">",
"        <p>",
"         32-35&deg;C",
"        </p>",
"        (90-95&deg;F)",
"       </td>",
"       <td>",
"        Shivering but conscious",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cyanosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prolonged capillary refill",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"5\">",
"        Moderate",
"       </td>",
"       <td rowspan=\"5\">",
"        <p>",
"         28-32&deg;C",
"        </p>",
"        (82-90&deg;F)",
"       </td>",
"       <td>",
"        Shivering stops, impaired consciousness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clumsiness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Confusion or delirium",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Slurred speech",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreasing BP",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"8\">",
"        Severe",
"       </td>",
"       <td rowspan=\"8\">",
"        <p>",
"         &lt;28&deg;C",
"        </p>",
"        (&lt;82&deg;F)",
"       </td>",
"       <td>",
"        No shivering, unconscious, vital signs may or may not be present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Muscle rigidity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Erythema and edema",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stupor or coma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Absent pulses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fixed, dilated pupils",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ventricular fibrillation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Asystole",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * When present, findings are typical of the stage shown, but may vary in their presence and their temperature of onset.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
