var title_f0_15_253="Radiologic Ddx IIP";
var content_f0_15_253=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F60070&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F60070&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Radiologic features and differential diagnosis of idiopathic interstitial pneumonias",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Clinical diagnosis",
"       </td>",
"       <td class=\"subtitle1\">",
"        Histologic pattern",
"       </td>",
"       <td class=\"subtitle1\">",
"        Radiographic features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Typical distribution",
"       </td>",
"       <td class=\"subtitle1\">",
"        Typical CT findings",
"       </td>",
"       <td class=\"subtitle1\">",
"        Differential diagnosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        IPF/CFA",
"       </td>",
"       <td>",
"        UIP",
"       </td>",
"       <td>",
"        Basal-predominant reticular abnormality with volume loss",
"       </td>",
"       <td>",
"        Peripheral, subpleural, basal",
"       </td>",
"       <td>",
"        Reticular, honeycombing, traction bronchiectasis/bronchiolectasis; architectural distortion. Focal ground glass",
"       </td>",
"       <td>",
"        Asbestosis, collagen vascular disease, hypersensitivity pneumonitis, sarcoidosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        NSIP",
"       </td>",
"       <td>",
"        NSIP",
"       </td>",
"       <td>",
"        Ground glass and reticular opacity",
"       </td>",
"       <td>",
"        Peripheral, subpleural, basal, symmetric",
"       </td>",
"       <td>",
"        Ground glass attenuation, irregular lines, consolidation",
"       </td>",
"       <td>",
"        UIP, DIP, COP, hypersensitivity pneumonitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        COP",
"       </td>",
"       <td>",
"        OP",
"       </td>",
"       <td>",
"        Patchy bilateral consolidation",
"       </td>",
"       <td>",
"        Subpleural/peribronchial",
"       </td>",
"       <td>",
"        Patchy consolidation and/or nodules",
"       </td>",
"       <td>",
"        Infection, vasculitis, sarcoidosis, alveolar carcinoma, lymphoma, eosinophilic pneumonia, NSIP",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        AIP",
"       </td>",
"       <td>",
"        DAD",
"       </td>",
"       <td>",
"        Progressive diffuse ground glass density/consolidation",
"       </td>",
"       <td>",
"        Diffuse",
"       </td>",
"       <td>",
"        Consolidation and ground glass opacity, often with lobular sparing. Traction bronchiectasis later",
"       </td>",
"       <td>",
"        Hydrostatic edema, pneumonia, acute eosinophilic pneumonia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        DIP",
"       </td>",
"       <td>",
"        DIP",
"       </td>",
"       <td>",
"        Ground glass opacity",
"       </td>",
"       <td>",
"        Lower zone, peripheral predominance in most",
"       </td>",
"       <td>",
"        Ground glass attenuation, reticular lines",
"       </td>",
"       <td>",
"        RB-ILD, hypersensitivity pneumonitis, sarcoidosis, PCP",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        RB-ILD",
"       </td>",
"       <td>",
"        RB",
"       </td>",
"       <td>",
"        Bronchial wall thickening; ground glass opacity",
"       </td>",
"       <td>",
"        Diffuse",
"       </td>",
"       <td>",
"        Bronchial wall thickening, cenrilobular nodules, patchy ground glass opacity",
"       </td>",
"       <td>",
"        DIP, NSIP, Hypersensitivity pneumonitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        LIP",
"       </td>",
"       <td>",
"        LIP",
"       </td>",
"       <td>",
"        Reticular opacities, nodules",
"       </td>",
"       <td>",
"        Diffuse",
"       </td>",
"       <td>",
"        Centrilobular nodules, ground glass attenuation, septal and bronchovascular thickening, thin-walled cysts",
"       </td>",
"       <td>",
"        Sarcoidosis, lymphangitic carcinoma, Langerhans' cell histiocytosis",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     AIP: acute interstitial pneumonia; CFA: cryptogenic fibrosing alveolitis; COP: cryptogenic OP (also called idiopathic bronchiolitis obliterans organizing pneumonia [BOOP]); DAD: diffuse alveolar damage; DIP: desquamative interstitial pneumonia; IPF: idiopathic pulmonary fibrosis; LIP: lymphoid interstitial pneumonia; NSIP: nonspecific interstitial pneumonia; OP: organizing pneumonia; PCP: Pneumocystis jirovecii (carinii) pneumonia; RB-ILD: respiratory bronchiolitis-associated intersitial lung disease: UIP: usual interstitial pneumonia.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
