var title_f0_16_269="Prevalence of UTI in febrile infants and children";
var content_f0_16_269=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F76804&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F76804&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Prevalence of urinary tract infection in febrile* infants and children by demographic group",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Demographic group",
"       </td>",
"       <td class=\"subtitle1\">",
"        Prevalence or pretest probability (95% CI)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        0 to 24 months",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        7.0 percent (5.5-8.4)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Girls",
"       </td>",
"       <td>",
"        7.3 percent (5.0-9.6)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        White girls with temperature &ge;39&deg;C",
"       </td>",
"       <td>",
"        16 percent",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Boys",
"       </td>",
"       <td>",
"        8.0 (5.5-10.4)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        White children",
"       </td>",
"       <td>",
"        8.0 percent (5.1-11)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Black children",
"       </td>",
"       <td>",
"        4.7 percent (2.1-7.3)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\">",
"        0 to 3 months",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        7.2 percent (5.8-8.6)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Girls",
"       </td>",
"       <td>",
"        7.5 percent (5.1-10)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Circumcised boys",
"       </td>",
"       <td>",
"        2.4 percent (1.4-3.5)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Uncircumcised boys",
"       </td>",
"       <td>",
"        20.1 percent (16.8-23.4)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\">",
"        3 to 6 months",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        6.6 percent (1.7-11.5)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Girls",
"       </td>",
"       <td>",
"        5.7 percent (2.3-9.4)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Boys",
"       </td>",
"       <td>",
"        3.3 percent (1.3-5.3)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\">",
"        6 to 12 months",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        5.4 percent (3.4-7.4)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Girls",
"       </td>",
"       <td>",
"        8.3 percent (3.9-12.7)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Boys",
"       </td>",
"       <td>",
"        1.7 percent (0.5-2.9)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\">",
"        12 to 24 months",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        4.5 percent",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Girls",
"       </td>",
"       <td>",
"        2.1 percent (1.2-3.6)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Circumcised boys &gt;1 year",
"       </td>",
"       <td>",
"        &lt;1 percent",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\">",
"        &lt;19 years with urinary symptoms and/or fever",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        7.8 percent (6.6-8.9)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Temperature &ge;38&deg;C.",
"     <br>",
"      <span class=\"bullet\">",
"       &bull;",
"      </span>",
"      95% confidence interval not available.",
"      <br>",
"       &Delta; Most of these children were older than two years.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Shaikh N, Morone NE, Bost JE, Farrell MH. Prevalence of Urinary Tract Infection in Childhood: A Meta-Analysis. Pediatr Infect Dis J 2008; 27:302.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
