" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pulmonary alveolar proteinosis in children",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?0/22/360/contributors\">",
