" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Frontotemporal dementia: Treatment",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?0/25/408/contributors\">",
