var title_f0_25_411="Bleomycin pneumonitis appearance";
var content_f0_25_411=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F75991&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F75991&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Radiographic manifestations of bleomycin-induced pneumonitis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Bilateral reticular or fine nodular infiltrate (50 percent)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Alveolar densities (10 percent)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Normal (10 percent)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nodular lesions with smooth or irregular borders which may cavitate (rare)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No pleural effusions or adenopathy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rare pneumothorax when honeycomb lung is present",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
