var title_f0_26_429="Diagnostic criteria for Carney complex";
var content_f0_26_429=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F83442&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F83442&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnostic criteria for Carney complex (CNC)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Major diagnostic criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Spotty skin pigmentation with typical distribution (lips, conjunctiva and inner or outer canthi, vaginal and penile mucosa)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Myxoma* (cutaneous and mucosal)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cardiac myxoma*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Breast myxomatosis* or fat-supressed magnetic resonance imaging findings suggestive of this diagnosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        PPNAD* or paroxical positive response of urninary glucocorticoid excretion to dexamethasone administration during Liddle's test",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acromegaly due to GH-producing adenoma*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        LCCSCT* or characteristic calcification on testicular ultrasound",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Thyroid carcinoma* or multiple, hypoechoic nodules on thyroid ultrasound in a young patient",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Psammomatous melanotic schwannomas*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Blue nevus, ephithelioid blue nevus*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Breast ductal adenoma*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Osteochondromyxoma*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Supplemental criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Affected first-degree relative",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inactivating mutation of the",
"        <em>",
"         PRKAR1A",
"        </em>",
"        gene",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Findings suggestive of or possibly associated with CNC, but not diagnostic for the disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Intense freckling (without darkly pigmented spots or typical distribution)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Blue nevus, common type (if multiple)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Caf&eacute;-au-lait spots or other \"birthmarks\"",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Elevated IGF-I levels, abnormal GTT, or paradoxical GH response to TRH testing in the absence of clinical acromegaly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cardiomyopathy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pilonidal sinus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        History of Cushing's syndrome, acromegaly, or sudden death in extended family",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Multiple skin tags or other skin lesions; lipomas",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Colonic polyps (usually in association with acromegaly)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hyperprolactinemia (usually mild and almost always combined with clinical or subclinical acromegaly)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Single, benign thyroid nodule in a young patient; multiple thyroid nodules in an older patient (detected on ultrasound)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Family history of carcinoma, in particular of the thyroid, colon, pancreas, and ovary; other multiple benign or malignant tumors.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CNC: Carney complex; PPNAD: primary pigmented nodular adrenocortical disease; LCCSCT: large-cell calcifying Sertoli cell tumor;",
"     <em>",
"      PRKAR1A",
"     </em>",
"     : protein kinase A regulatory subunit 1&alpha;.",
"     <br/>",
"     * After histological confirmation.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Almeida MQ, Stratakis CA. Carney complex and other conditions associated with micronodular adrenal hyperplasias. Best Pract Res Clin Endocrinol Metab 2010; 24:907. Table used with the permission of Elsevier Inc. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
