" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Developmental defects of the teeth",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?0/27/440/contributors\">",
