var title_f0_3_52="HBV consensus definitions";
var content_f0_3_52=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F81182&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F81182&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Glossary of clinical terms used in HBV infection",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Definitions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Chronic hepatitis B",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Chronic necroinflammatory disease of the liver caused by persistent infection with hepatitis B virus. Chronic hepatitis B can be subdivided into HBeAg positive and HBeAg negative chronic hepatitis B.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Inactive HBsAg carrier state",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Persistent HBV infection of the liver without significant, ongoing necroinflammatory disease.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Resolved hepatitis B",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Previous HBV infection without further virological, biochemical or histological evidence of active virus infection or disease.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Acute exacerbation or flare of hepatitis B",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Intermittent elevations of aminotransferase activity to more than 10 times the upper limit of normal and more than twice the baseline value.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Reactivation of hepatitis B",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Reappearance of active necroinflammatory disease of the liver in a person known to have the inactive HBsAg carrier state or resolved hepatitis B.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        HBeAg clearance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Loss of HBeAg in a person who was previously HBeAg positive.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        HBeAg seroconversion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Loss of HBeAg and detection of anti-HBe.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
