var title_f0_31_508="Causes of hemolytic anemia in children";
var content_f0_31_508=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F64454&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F64454&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Causes of hemolytic anemia in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Intrinsic red blood cell defects",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Enzyme deficiencies (eg, G6PD or pyruvate kinase deficiencies)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hemoglobinopathies (eg, sickle cell disease, thalassemias, unstable hemoglobins)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Membrane defects (eg, hereditary spherocytosis, elliptocytosis)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Extrinsic red blood cell defects",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Auto-immune hemolytic anemia (AIHA)",
"        <ul>",
"         <li>",
"          Warm-reactive",
"         </li>",
"         <li>",
"          Cold-reactive (paroxysmal cold hemoglobinuria or cold agglutinin disease)",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Liver disease (eg, cirrhosis, hypersplenism, acquired disorders of the red cell membrane [see topic review, \"Extrinsic nonimmune hemolytic anemia due to systemic disease\"])",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypersplenism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Oxidant agents (eg, dapsone, nitrites, aniline dyes)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Microangiopathies",
"        <ul>",
"         <li>",
"          Hemolytic-uremic syndrome (HUS)",
"         </li>",
"         <li>",
"          Disseminated intravascular coagulation (DIC)",
"         </li>",
"         <li>",
"          Artificial heart valves",
"         </li>",
"         <li>",
"          Kasabach-Merritt phenomena (destruction of platelets and red blood cells within a vascular tumor)",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Paroxysmal cold hemoglobinuria (PCH)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Paroxysmal nocturnal hemoglobinuria (PNH)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Use of anti-D immune globulin",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     G6PD: glucose-6-phosphate deficiency.",
"    </div>",
"    <div class=\"reference\">",
"     Courtesy of Dr. Michael Recht.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
