var title_f0_31_509="Pleural effusions in HIV II";
var content_f0_31_509=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F71626&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F71626&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Pleural effusions in HIV: Differential diagnosis and diagnostic testing (continued)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Etiology",
"      </td>",
"      <td class=\"subtitle1\">",
"       History/physical exam/lab findings",
"      </td>",
"      <td class=\"subtitle1\">",
"       Pleural fluid characteristics",
"      </td>",
"      <td class=\"subtitle1\">",
"       Additional tests of pleural fluid",
"      </td>",
"      <td class=\"subtitle1\">",
"       Other helpful tests",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Kaposi sarcoma (KS) associated effusion",
"      </td>",
"      <td>",
"       <p>",
"        Skin lesions of KS",
"       </p>",
"       <p>",
"        No pleuritic pain",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        Large to massive, bilateral effusion(s)",
"       </p>",
"       <p>",
"        Hemorrhagic fluid",
"       </p>",
"      </td>",
"      <td>",
"       Cytology is negative",
"      </td>",
"      <td>",
"       <p>",
"        Chest radiograph: diffuse pulmonary opacities",
"       </p>",
"       <p>",
"        Endobronchial lesions of KS",
"       </p>",
"       <p>",
"        Pleural biopsy is negative",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Multicentric Castleman disease",
"      </td>",
"      <td>",
"       <p>",
"        Fever",
"       </p>",
"       <p>",
"        Cough, dyspnea in 60 percent",
"       </p>",
"       <p>",
"        Peripheral lymphadenopathy",
"       </p>",
"       <p>",
"        Anemia",
"       </p>",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       <p>",
"        Chest radiograph: reticular or nodular opacities",
"       </p>",
"       <p>",
"        Chest CT: mediastinal adenopathy as disease progresses",
"       </p>",
"       <p>",
"        Lymph node biopsy",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Systemic lymphoma",
"      </td>",
"      <td>",
"       <p>",
"        Low CD4 level",
"       </p>",
"       <p>",
"        Serum LDH high",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        Large, serosanguinous exudative effusion (may have mass effect)",
"       </p>",
"       <p>",
"        Pleural LDH high",
"       </p>",
"       <p>",
"        Cytology positive (60 percent)",
"       </p>",
"      </td>",
"      <td>",
"       Flow cytometry",
"      </td>",
"      <td>",
"       Pleural biopsy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Primary effusion lymphoma",
"      </td>",
"      <td>",
"       <p>",
"        Low CD 4 count",
"       </p>",
"       <p>",
"        Preexisting KS in 30 percent",
"       </p>",
"      </td>",
"      <td>",
"       Pleural fluid cytology usually diagnostic",
"      </td>",
"      <td>",
"       <p>",
"        LANA-1 (HHV8) +",
"       </p>",
"       <p>",
"        Lymphocytes often:",
"       </p>",
"       <p>",
"        CD45 +/CD30 +/EMA -/T cell antigen -/LMP1 usually -",
"       </p>",
"       <p>",
"        Monoclonal B cell population by Southern blot",
"       </p>",
"      </td>",
"      <td>",
"       <p>",
"        Chest CT: adjacent parietal pleural thickening and absent lymphadenopathy",
"       </p>",
"       <p>",
"        Pericardial effusion and ascites (common)",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Thromboembolism",
"      </td>",
"      <td>",
"       Risk factor(s) for thromboembolism",
"      </td>",
"      <td>",
"       Exudative, may be serous or serosanguinous",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       <p>",
"        d-Dimer (positive)",
"       </p>",
"       <p>",
"        CT pulmonary angiogram",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Hypoalbuminemia",
"      </td>",
"      <td>",
"       Bilateral peripheral edema",
"      </td>",
"      <td>",
"       Transudative",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       Serum albumin (low)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Heart failure",
"      </td>",
"      <td>",
"       <p>",
"        Chronic dyspnea",
"       </p>",
"       <p>",
"        Bilateral peripheral edema",
"       </p>",
"      </td>",
"      <td>",
"       Transudative",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       <p>",
"        Serum BNP (elevated)",
"       </p>",
"       <p>",
"        Echocardiogram",
"       </p>",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     EMA: epithelial membrane antigen; LANA-1: as latency-associated nuclear antigen, a latent HHV8 gene product; LMP1: latent membrane protein 1 of Epstein Barr virus.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
