var title_f0_37_604="Natural history of chronic AR";
var content_f0_37_604=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F54569&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F54569&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Natural history of chronic aortic regurgitation mostly based upon data from nine series with a total of 593 patients followed for a mean of 6.6 years",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_left\" colspan=\"2\" rowspan=\"1\">",
"        Asymptomatic patients with normal left ventricular (LV) systolic function",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Progression to symptoms and/or LV dysfunction",
"       </td>",
"       <td>",
"        Less than 6 percent/year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"2\" rowspan=\"1\">",
"        Progression to symptoms, LV dysfunction, or death",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\" colspan=\"2\" rowspan=\"1\">",
"        According to LV end-systolic dimension*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        &gt;50 mm",
"       </td>",
"       <td class=\"sublist_other\">",
"        19 percent/year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        40 to 50 mm",
"       </td>",
"       <td class=\"sublist_other\">",
"        6 percent/year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        &lt;40 mm",
"       </td>",
"       <td class=\"sublist_other\">",
"        0 percent/year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Progression to asymptomatic LV dysfunction",
"       </td>",
"       <td colspan=\"1\">",
"        Less than 3.5 percent/year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sudden death",
"       </td>",
"       <td colspan=\"1\">",
"        Less than 0.2 percent/year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\" colspan=\"2\" rowspan=\"1\">",
"        Asymptomatic patients with LV systolic dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Progression to cardiac symptoms",
"       </td>",
"       <td colspan=\"1\">",
"        More than 25 percent/year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\" colspan=\"2\" rowspan=\"1\">",
"        Symptomatic patients",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mortality rate",
"       </td>",
"       <td colspan=\"1\">",
"        More than 10 percent/year",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from Bonow, RO, Lakatos, E, Maron, BJ, et al. Serial long-term assessment of the natural history of asymptomatic patients with chronic aortic regurgitation and normal left ventricular systolic function. Circulation 1991; 84:1625.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
