" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Clinical features of autism spectrum disorders",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?0/41/664/contributors\">",
