var script_f0_43_700=[""].join("\n");
var outline_f0_43_700=null;
var title_f0_43_701="Comparison of pneumococcal conjugate and polysaccharide vaccines";
var content_f0_43_701=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F71746&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F71746&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Comparison of pneumococcal conjugate and pneumococcal polysaccharide vaccines used in children in the United States",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"20%\">",
"     </colgroup>",
"     <colgroup span=\"2\" width=\"40%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        13-valent pneumococcal conjugate vaccine",
"        <br/>",
"        (PCV13)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Pneumococcal polysaccharide vaccine",
"        <br/>",
"        (PPSV23)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Trade name",
"        </strong>",
"       </td>",
"       <td>",
"        Prevnar 13&trade;",
"       </td>",
"       <td>",
"        Pneumovax",
"        <sup>",
"         &reg;",
"        </sup>",
"        23",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Serotypes contained",
"        </strong>",
"       </td>",
"       <td>",
"        1, 3, 4, 5, 6A, 6B, 7F, 9V, 14, 18C, 19A, 19F, 23F",
"       </td>",
"       <td>",
"        1, 2, 3, 4, 5, 6B, 7F, 8, 9N, 9V, 10A, 11A, 12F, 14, 15B, 17F, 18C, 19A, 19F, 20, 22F, 23F, 33F",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Protein carrier",
"        </strong>",
"       </td>",
"       <td>",
"        CRM197 (a nontoxic mutant of diphtheria toxin)",
"       </td>",
"       <td>",
"        None",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Adjuvant",
"        </strong>",
"       </td>",
"       <td>",
"        Aluminum phosphate",
"       </td>",
"       <td>",
"        None",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Contains thimerosal?",
"        </strong>",
"       </td>",
"       <td>",
"        No",
"       </td>",
"       <td>",
"        May contain thimerosal as a preservative",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Dose",
"        </strong>",
"       </td>",
"       <td>",
"        0.5 mL",
"       </td>",
"       <td>",
"        0.5 mL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Route",
"        </strong>",
"       </td>",
"       <td>",
"        Intramuscular",
"       </td>",
"       <td>",
"        Intramuscular or subcutaneous",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Target groups",
"        </strong>",
"       </td>",
"       <td>",
"        All infants and children younger than five years",
"       </td>",
"       <td>",
"        Individuals &ge;2 years at increased risk for pneumococcal disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Approved age groups",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         6 weeks through 17 years;",
"        </p>",
"        &ge;50 years",
"       </td>",
"       <td>",
"        &ge;2 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Number of doses",
"        </strong>",
"       </td>",
"       <td>",
"        Varies depending on age when series is initiated",
"       </td>",
"       <td>",
"        One or two depending upon risk group",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Recommended schedule",
"        </strong>",
"       </td>",
"       <td>",
"        2, 4, 6, and 12 to 15 months",
"       </td>",
"       <td>",
"        <p>",
"         One dose of PPSV23 at age &ge;2 years and &ge;8 weeks after the last indicated dose of PCV13.",
"        </p>",
"        <p>",
"         A second dose (five years after the first) is recommended for children who are immunocompromised, have sickle cell disease, or functional or anatomic asplenia.",
"        </p>",
"        A second dose is",
"        <strong>",
"         not",
"        </strong>",
"        recommended for immunocompetent children with cochlear implants or chronic illness, including chronic heart disease, chronic lung disease, diabetes mellitus, and cerebrospinal fluid leaks.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         May be administered with other vaccines?",
"        </strong>",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
