var title_f0_45_726="NHLBI pediatric risk stratification associated with CVD";
var content_f0_45_726=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F54963&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F54963&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\" style=\"width: 626px\">",
"   <div class=\"ttl\">",
"    Risk stratification and management for children with conditions predisposing to accelerated atherosclerosis and early cardiovascular disease from the 2011 NHLBI Expert Panel",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 606px; height: 702px; background-image: url(data:image/gif;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
"    <strong>",
"     Step 1:",
"    </strong>",
"    Risk stratification by disease state or condition.",
"    <br/>",
"    <strong>",
"     Step 2:",
"    </strong>",
"    Assess all cardiovascular risk factors. If there are &ge;2 comorbidities, move tier II patient to tier I for subsequent management.",
"    <br/>",
"    <strong>",
"     Step 3:",
"    </strong>",
"    Tier-specific treatment goals/cut points defined.",
"    <br/>",
"    <strong>",
"     Step 4:",
"    </strong>",
"    Initial therapy: For tier I, initial management is therapeutic lifestyle change PLUS disease-specific management. For tier II, initial management is therapeutic lifestyle change.",
"    <br/>",
"    <strong>",
"     Step 5:",
"    </strong>",
"    For tier I conditions, if goals are not met, consider pharmacological therapy.",
"    <div class=\"footnotes\">",
"     CVD: cardiovascular disease; NHLBI: National Heart, Lung, and Blood Institute; CV: cardiovascular; LDL-C: low-density lipoprotein cholesterol; HDL-C: high-density lipoprotein cholesterol; TG: triglycerides; BP: blood pressure; BMI: body mass index; HgbA1c: hemoglobin A1c.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Daniels SR, Benuck I, Christakis DA, et al. Expert panel on integrated guidelines for cardiovascular health and risk reduction in children and adolescents: Full report, 2011. National Heart Lung and Blood Institute. Available at: file://www.nhlbi.nih.gov/guidelines/cvd_ped/peds_guidelines_full.pdf.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
