var title_f0_5_86="ICAR-MEDCOM hypothermia scale";
var content_f0_5_86=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F88073&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F88073&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ICAR-MEDCOM hypothermia (HT) scale",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" colspan=\"3\">",
"        International Commission for Mountain Emergency Medicine hypothermia scale",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2\">",
"        Hypothermia (HT) stage",
"       </td>",
"       <td class=\"subtitle2\">",
"        Clinical assessment",
"       </td>",
"       <td class=\"subtitle2\">",
"        Expected core temperature, C&deg; (F&deg;)*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        HT I",
"       </td>",
"       <td>",
"        Clear consciousness with shivering",
"       </td>",
"       <td class=\"centered\">",
"        35-32 (95-89.6)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        HT II",
"       </td>",
"       <td>",
"        Impaired consciousness without shivering",
"       </td>",
"       <td class=\"centered\">",
"        32-28 (89.6-82.4)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        HT III",
"       </td>",
"       <td>",
"        Unconsciousness (circulation present)",
"       </td>",
"       <td class=\"centered\">",
"        28-24 (82.4-75.2)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        HT IV",
"       </td>",
"       <td>",
"        Apparent death (circulation absent)",
"       </td>",
"       <td class=\"centered\">",
"        &lt;24-13.7 (&lt;75.2-56.7)",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        HT V",
"       </td>",
"       <td>",
"        Death due to irreversible hypothermia",
"       </td>",
"       <td class=\"centered\">",
"        &lt;13.7-9 (56.7-48.2)",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Clinical assessment may be used alone to stage hypothermia when core temperature measurement in the field is not available.",
"     <br>",
"      <font class=\"bullet\">",
"       &bull;",
"      </font>",
"      The core temperature at which irreversible hypothermia with death occurs is not well defined. Please see the UpToDate topics on hypotherma for discussions regarding prehospital declaration of death and withdrawal of resuscitation.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Modified with permission from: Durrer B, Brugger H, Syme D, and the International Commission for Mountain Emergency Medicine. The medical on-site treatment of hypothermia: ICAR-MEDCOM recommendation. High Alt Med Biol 2003; 4:99. Copyright &copy; 2003 Mary Ann Liebert, Inc.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
