"   <h1>",
"    REFERENCES",
"   </h1>",
"   <ol id=\"reference\">",
"    <li>",
"     <div class=\"reference\">",
"      Adrogu&eacute; HJ and Madias NE, \"Management of Life-Threatening Acid-Base Disorders. Second of Two Parts,\"",
"      <i>",
"       N Engl J Med",
"      </i>",
"      , 1998, 338(2):107-11.",
"      <span class=\"pubmed-id\">",
"       [PubMed",
"       <a href=\"UTD.htm?0/52/840/abstract-text/9420343/pubmed\" id=\"9420343\" target=\"_blank\">",
"        9420343",
"       </a>",
"       ]",
"      </span>",
"     </div>",
