" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Euthyroid hyperthyroxinemia and hypothyroxinemia",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?0/53/856/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?0/53/856/contributors\">",
"     Douglas S Ross, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?0/53/856/contributors\">",
"     Section Editor",
"    </a>",
