" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Erythematous disorders of the breast",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?0/53/857/contributors\">",
