var title_f0_53_860="DSM-IV criteria gambling";
var content_f0_53_860=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PSYCH%2F59506&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PSYCH%2F59506&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnostic criteria for pathological gambling",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       A. Persistent and recurrent maladaptive gambling behavior as indicated by five (or more) of the following:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       1. Is preoccupied with gambling (eg, preoccupied with reliving past gambling experiences, handicapping or planning the next venture, or thinking of ways to get money with which to gamble)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       2. Needs to gamble with increasing amounts of money in order to achieve the desired excitement",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       3. Has repeated unsuccessful efforts to control, cut back, or stop gambling",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       4. Is restless or irritable when attempting to cut down or stop gambling",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       5. Gambles as a way of escaping from problems or of relieving a dysphoric mood (eg, feelings of helplessness, guilt, anxiety, depression)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       6. After losing money gambling, often returns another day to get even (\"chasing one's losses\")",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       7. Lies to family members, therapist, or others to conceal the extent of involvement with gambling",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       8. Has committed illegal acts such as forgery, fraud, theft, or embezzlement to finance gambling",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       9. Has jeopardized or lost a significant relationship, job, or educational or career opportunity because of gambling",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       10. Relies on others to provide money to relieve a desperate financial situation caused by gambling",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       B. The gambling behavior is not better accounted for by a manic episode",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from the Diagnostic ad Statistical Manual of Mental Disorders, Text Revision, Fourth Edition (Copyright 2000). American Psychiatric Association.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
