var title_f0_57_924="Management of women with previous PTD or 2nd trimester loss";
var content_f0_57_924=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F71782&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=10\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F71782&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=10\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Management of women with previous preterm delivery or second trimester loss",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Patient population",
"       </td>",
"       <td class=\"subtitle1\">",
"        Interventions to be considered",
"       </td>",
"       <td class=\"subtitle1\">",
"        Options in next pregnancy if history-indicated cerclage was not successful (ie, preterm birth &lt;33 weeks)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Women with:",
"        <br/>",
"        <ul>",
"         <li>",
"          &ge;2 consecutive prior second trimester losses*,",
"          <strong>",
"           or",
"          </strong>",
"         </li>",
"         <li>",
"          &ge;3 early (&lt;34 weeks) preterm births",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        <p>",
"         Transvaginal cerclage at 12 to 14 weeks, and",
"        </p>",
"        <p>",
"         17-alpha-hydroxyprogesterone caproate 250 mg IM weekly from 16 to 36 weeks",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Transabdominal cerclage",
"        </p>",
"        <p>",
"         17-alpha-hydroxyprogesterone caproate 250 mg IM weekly from 16 to 36 weeks",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Women with:",
"        <br/>",
"        <ul>",
"         <li>",
"          One prior second trimester loss*,",
"          <strong>",
"           or",
"          </strong>",
"         </li>",
"         <li>",
"          One or two preterm births &lt;37 weeks",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        <p>",
"         17-alpha-hydroxyprogesterone caproate 250 mg IM weekly from 16 to 36 weeks",
"        </p>",
"        <p>",
"         Serial measurement of cervical length beginning at 14 to 16 weeks and ending at 24 weeks (see table entitled \"Protocol for cervical length screening\")",
"        </p>",
"        <p>",
"         If cervical length &lt;25 mm before 24 weeks, place transvaginal cerclage",
"        </p>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Usually a spontaneous pregnancy loss between 16 and 27 6/7ths weeks of gestation.",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Iams JD, Berghella V. Care for women with prior preterm birth. Am J Obstet Gynecol 2010; 203:89.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
