" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Risk factors for morbidity and mortality in patients with obstructive jaundice undergoing surgery",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Azotemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cholangitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hematocrit &lt; 30 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypoalbulminemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Malignant cause of biliary obstruction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Serum bilirubin level &gt; 11 mg/dL (188 &micro;mol/liter)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
