var title_f0_60_972="Motor automatisms";
var content_f0_60_972=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F61962&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F61962&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Motor automatisms in Neonatal seizures",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Ocular signs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        Random and roving eye movements or nystagmus (distinct from tonic eye deviation)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        May be provoked or intensified by tactile stimulation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Presumed pathophysiology: nonepileptic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Oral-buccal-lingual movements",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sucking, chewing, tongue protrusions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        May be provoked or intensified by stimulation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Presumed pathophysiology: nonepileptic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Progression movements",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rowing or swimming movements",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        Pedaling or bicycling movements of the legs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        May be provoked or intensified by stimulation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        May be suppressed by restraint or repositioning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Presumed pathophysiology: nonepileptic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Complex purposeless movements",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        Sudden arousal with transient increased random activity of limbs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        May be provoked or intensified by stimulation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Presumed pathophysiology: nonepileptic",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted with permission from: Mizrahi, EM, Kellaway, P. Diagnosis and Management of Neonatal Seizures. Lippincott-Raven, Philadelphia 1998. p.181. Copyright &copy; 1998 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
