var title_f0_61_989="Management of pediatric lead poisoning";
var content_f0_61_989=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F74425&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F74425&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Guidance for treatment actions for pediatric lead poisoning according to blood lead level (BLL)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Blood lead level (microg/dL)*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Treatment actions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        &lt;10",
"       </td>",
"       <td>",
"        Provide lead education and regular developmental/behavioral surveillance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reassess or rescreen in&nbsp;one year, or sooner if the child is younger than two years, was tested at the onset of warm weather, is at high risk for lead exposure, or the exposure sources have changed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        10 - 14",
"       </td>",
"       <td>",
"        Provide lead education and regular developmental/behavioral surveillance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Provide follow-up testing within one month, then every&nbsp;three to&nbsp;four months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"5\">",
"        15 - 19",
"       </td>",
"       <td>",
"        Provide lead education and regular developmental/behavioral surveillance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Provide follow-up testing within one month, then every&nbsp;three to four months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Refer to social services",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Initiate professional environmental cleanup",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Follow guidelines for 20 - 44 g/dL if BLL remains &ge;15 microg/dL (on two samples obtained at least three months apart)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"6\">",
"        20 - 44",
"       </td>",
"       <td>",
"        Provide lead education and regular developmental/behavioral surveillance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Refer to clinical center specializing in lead poisoning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Provide case management",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Provide both clinical and environmental management",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ensure lead hazard control",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Consider treating with DMSA",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"8\">",
"        45 - 69",
"       </td>",
"       <td>",
"        Provide lead education",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Refer to clinical center specializing in lead poisoning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Provide coordination of care",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Provide diagnostic testing within 24 to 48 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Perform clinical evaluation and management within 48 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Provide appropriate chelation therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Perform aggressive environmental intervention",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Provide follow-up testing at least once per month (in accordance with chelation therapy) and regular developmental/behavioral surveillance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"4\">",
"        &ge;70",
"       </td>",
"       <td>",
"        This is a medical emergency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Perform diagnostic testing immediately (samples shouldbe processed emergently)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hospitalize immediately and begin chelation therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Begin other activities as above",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Based upon confirmatory venousblood lead level.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Roberts JR, Reigart JR. Medical assessment and interventions. In: Managing Elevated Blood Lead Levels Among Young children:Recommendations from the Advisory Committee on Childhood Lead PoisoningPrevention. Centers for Disease Control and Prevention, Atlanta, GA, 2002.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
