" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Classification of lower extremity peripheral artery disease",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?1/0/1031/contributors\">",
"     Author",
"    </a>",
