var title_f1_14_1261="Caries periodontitis treatment";
var content_f1_14_1261=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F77718&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F77718&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Antimicrobial regimens for the prevention of dental caries and the treatment of periodontal disease in adults",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Clinical entity",
"      </td>",
"      <td class=\"subtitle1\">",
"       Common causative organisms",
"      </td>",
"      <td class=\"subtitle1\">",
"       Antimicrobial regimens",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"1\" rowspan=\"3\">",
"       Supragingival dental plaque and dental caries prevention",
"      </td>",
"      <td colspan=\"1\" rowspan=\"3\">",
"       <em>",
"        Streptococcus mutans",
"       </em>",
"       , other streptococci,",
"       <em>",
"        Actinomyces",
"       </em>",
"       spp",
"      </td>",
"      <td>",
"       Fluoride-containing toothpaste (sodium fluoride, 1.1 percent or stannous fluoride, 0.4 percent) 2 or 3 times daily",
"       <strong>",
"        AND/OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Fluoride-containing varnishes (sodium fluoride, 5 percent) applied 3 or 4 times yearly",
"       <strong>",
"        AND/OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Chlorhexidine, 0.12 percent oral rinse",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"3\" rowspan=\"1\">",
"       Gingivitis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"4\">",
"       Acute simple gingivitis",
"      </td>",
"      <td rowspan=\"4\">",
"       Streptococci,",
"       <em>",
"        Actinomyces",
"       </em>",
"       spp, spirochetes",
"      </td>",
"      <td>",
"       Penicillin G 2-4 MU IV every 4-6 hours (OR penicillin V 500 mg every 6-8 hours),",
"       <strong>",
"        PLUS",
"       </strong>",
"       metronidazole 500 mg PO every 8 hours",
"       <strong>",
"        OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Amoxicillin-clavulanate 875 mg PO every 12 hours or 500 mg PO every 8 hours",
"       <strong>",
"        OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Ampicillin-sulbactam 1.5-3 g IV every 6 hours",
"       <strong>",
"        OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Clindamycin 450 mg PO or 600 mg IV every 6-8 hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" rowspan=\"4\">",
"       Ulcerative or acute necrotizing ulcerative gingivitis",
"      </td>",
"      <td rowspan=\"4\">",
"       <em>",
"        Prevotella intermedia",
"       </em>",
"       ,",
"       <em>",
"        Fusobacterium",
"       </em>",
"       spp,",
"       <em>",
"        Tannerella forsythia",
"       </em>",
"       ,",
"       <em>",
"        Treponema denticoli",
"       </em>",
"       , other oral spirochetes",
"      </td>",
"      <td>",
"       Metronidazole 500 mg PO or IV every 8 hours",
"       <strong>",
"        OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Amoxicillin-clavulanate 875 mg PO every 12 hours or 500 mg PO every 8 hours",
"       <strong>",
"        OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Ampicillin-sulbactam 1.5-3 g IV every 6 hours",
"       <strong>",
"        OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Clindamycin 450 mg PO or 600 mg IV every 6-8 hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"3\" rowspan=\"1\">",
"       Periodontitis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"1\" rowspan=\"2\">",
"       Early onset, \"aggressive\" or \"localized juvenile\" periodontitis",
"      </td>",
"      <td colspan=\"1\" rowspan=\"2\">",
"       Actinobacillus actinomycetemcomitans, Porphyromonas gingivalis, Treponema denticola, Prevotella intermedia",
"      </td>",
"      <td>",
"       Doxycycline 200 mg PO or IV every 12 hours (only in patients 8 years of age or older)",
"       <strong>",
"        OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Metronidazole 500 mg PO or IV every 8 hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"1\" rowspan=\"2\">",
"       Adult periodontitis",
"      </td>",
"      <td rowspan=\"2\">",
"       Treponema denticoli, other oral spirochetes, Porphyromonas gingivalis, Prevotella intermedia, Tannerella forsythia",
"      </td>",
"      <td>",
"       Topical minocycline microspheres (Aristin&reg;)",
"       <strong>",
"        OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Topical doxycycline hyclate periodontal extended-release liquid (Atridox&reg;)",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
