"    <table cellspacing=\"0\">",
"     <tr>",
"      <td>",
"       Pain relieved with defecation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       More frequent stools at the onset of pain",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Looser stools at the onset of pain",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Visible abdominal distention",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Passage of mucus",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Sensation of incomplete evacuation",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
