" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Clinical manifestations of primary hyperparathyroidism",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?1/19/1338/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?1/19/1338/contributors\">",
"     Ghada El-Hajj Fuleihan, MD, MPH",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?1/19/1338/contributors\">",
"     Shonni J Silverberg, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?1/19/1338/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?1/19/1338/contributors\">",
"     Clifford J Rosen, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?1/19/1338/contributors\">",
