" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Actions of cholecystokinin",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Contracts the gallbladder",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stimulates pancreatic enzyme secretion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Delays gastric emptying",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Induces satiety",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Potentiates insulin secretion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Regulates intestinal motility",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Neurotransmitter",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
