" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Initial hormone therapy for metastatic prostate cancer",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?1/20/1353/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
