var title_f1_22_1388="Localized scleroderma";
var content_f1_22_1388=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F60398&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F60398&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification of localized scleroderma",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Circumscribed (plaque) morphea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        One or more circumscribed patches of skin with sclerotic changes in one anatomic site",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Involvement is confined to the superificial panniculus (subcutaneous tissue)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Generalized morphea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Four or more plaques that affect at least two anatomic sites",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Often located on the trunk or limbs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Similar clinical and histologic features as circumscribed morphea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Bullous morphea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bullous involvement",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        May occur in other forms of morphea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Linear morphea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Most common form of morphea in children",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        One or more elongated sclerotic areas of skin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lesions are typically asymmetric and oriented along the affected limb",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Associated with growth impairment of the involved extremity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lesions on the face or scalp are called en coup de sabre",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Deep morphea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Least common form of morphea in children",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Most disabling form of morphea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Primary site of involvement is the panniculus (subcutaneous tissue)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
