var title_f1_22_1389="Catheters used for venous and arterial access";
var content_f1_22_1389=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F75247&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F75247&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Catheters used for venous and arterial access",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Catheter type",
"       </td>",
"       <td class=\"subtitle1\">",
"        Entry site",
"       </td>",
"       <td class=\"subtitle1\">",
"        Length",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Peripheral venous catheters",
"       </td>",
"       <td>",
"        Usually inserted in veins of forearm or hand",
"       </td>",
"       <td>",
"        &lt;3 inches",
"       </td>",
"       <td>",
"        Phlebitis with prolonged use; rarely associated with bloodstream infection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Peripheral arterial catheters",
"       </td>",
"       <td>",
"        Usually inserted in radial artery; can be placed in femoral, axillary, brachial, posterior tibial arteries",
"       </td>",
"       <td>",
"        &lt;3 inches",
"       </td>",
"       <td>",
"        Low infection risk; rarely associated with bloodstream infection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Midline catheters",
"       </td>",
"       <td>",
"        Inserted via the antecubital fossa into the proximal basilic or cephalic veins; does not enter central veins, peripheral catheters",
"       </td>",
"       <td>",
"        3 to 8 inches",
"       </td>",
"       <td>",
"        Anaphylactoid reactions have been reported with catheters made of elastomeric hydrogel; lower rates of phlebitis than short peripheral catheters",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nontunneled central venous catheters",
"       </td>",
"       <td>",
"        Percutaneously inserted into central veins (subclavian, internal jugular, or femoral)",
"       </td>",
"       <td>",
"        &ge;8 cm depending on patient size",
"       </td>",
"       <td>",
"        Account for majority of CRBSI",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pulmonary artery catheters",
"       </td>",
"       <td>",
"        Inserted through a Teflon",
"        <sup>",
"         &reg;",
"        </sup>",
"        introducer in a central vein (subclavian, internal jugular, or femoral)",
"       </td>",
"       <td>",
"        &ge;30 cm depending on patient size",
"       </td>",
"       <td>",
"        Usually heparin bonded; similar rates of bloodstream infection as CVCs; subclavian site preferred to reduce infection risk",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Peripherally inserted central venous catheters (PICC)",
"       </td>",
"       <td>",
"        Inserted into basilic, cephalic, or brachial veins and enter the superior vena cava",
"       </td>",
"       <td>",
"        &ge;20 cm depending on patient size",
"       </td>",
"       <td>",
"        Lower rate of infection than nontunneled CVCs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tunneled central venous catheters",
"       </td>",
"       <td>",
"        Implanted into subclavian, internal jugular, or femoral veins",
"       </td>",
"       <td>",
"        &ge;8 cm depending on patient size",
"       </td>",
"       <td>",
"        Cuff inhibits migration of organisms into catheter tract; lower rate of infection than nontunneled CVC",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Totally implantable",
"       </td>",
"       <td>",
"        Tunneled beneath skin and have subcutaneous port accessed with a needle; implanted in subclavian or internal jugular vein",
"       </td>",
"       <td>",
"        &ge;8 cm depending on patient size",
"       </td>",
"       <td>",
"        Lowest risk for CRBSI; improved patient self-image; no need for local catheter-site care; surgery required for catheter removal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Umbilical catheters",
"       </td>",
"       <td>",
"        Inserted into either umbilical vein or umbilical artery",
"       </td>",
"       <td>",
"        &le;6 cm depending on patient size",
"       </td>",
"       <td>",
"        Risk for CRBSI similar with catheters placed in umbilical vein versus artery",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CRBSI: catheter related bloodstream infection; CVC: central venous catheter.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: O'Grady NP, Alexander M, Burns LA, et al. Guidelines for the prevention of intravascular catheter-related infections. Clin Infect Dis 2011; 52:e162, by permission of Oxford University Press. Copyright &copy; 2011.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
