var title_f1_23_1404="Types genital tract anomalies";
var content_f1_23_1404=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F50939&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F50939&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Acien classification of genital tract anomalies",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"1\">",
"        Agenesis or hypoplasia of the entire urogenital ridge",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        Unicornuate uterus with uterine, tubal, ovarian, and renal agenesis on the contralateral side",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"1\">",
"        Mesonephric anomalies with",
"absence of both opening of the Wolffian duct to the urogenital sinus",
"and ureteral bud sprouting leading to utero-vaginal duplicity and a",
"blind hemivagina ipsilateral with renal agenesis:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Unilateral hematocolpos",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        Gartner's pseudocyst on the anterolateral wall of the vagina",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        Partial reabsorption of intervaginal septum",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        Vaginal",
"or complete cervico-vaginal unilateral agenesis, ipsilateral with the",
"renal agenesis, and (1) with no communication, or (2) with",
"communication between both hemiuteri",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Isolated M&uuml;llerian anomalies:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        M&uuml;llerian ducts: unicornuate (generally, with rudimentary uterine horn), bicornuate, septate and didelphys uterus.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        M&uuml;llerian tubercle: cervico-vaginal atresia and segmentary anomalies such as transverse vaginal septum.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        M&uuml;llerian tubercle and ducts: (uni- or bilateral) Mayer-Rokitansky-Kuster-Hauser syndrome.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Anomalies of the urogenital sinus:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cloacal anomalies etc.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Malformative combinations:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\">",
"        Wolfian, M&uuml;llerian and cloacal anomalies.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Acien, P, Acien, M, Sanchez-Ferrer, M. Human Reproduction 2004; 19:2377.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
