var title_f1_24_1420="Examination of the septic appearing infant";
var content_f1_24_1420=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F63744&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F63744&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Linking physical examination and diagnosis in the septic-appearing infant",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Physical findings",
"       </td>",
"       <td class=\"subtitle1\">",
"        Likely diagnosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bulging fontanelle, fever",
"       </td>",
"       <td>",
"        Meningitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bulging fontanelle, no fever",
"       </td>",
"       <td>",
"        Hydrocephalus as with inflicted head injury",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Skin vesicles",
"       </td>",
"       <td>",
"        Herpes simplex virus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Temperature &gt;39&deg;C, female or an uncircumcised male",
"       </td>",
"       <td>",
"        UTI",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Weak cry, hypotonia, hyporeflexic, diminished or absent gag reflex, ptosis, mydriasis, weak suck, opthalmoparesis",
"       </td>",
"       <td>",
"        Infant botulism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pyloric tumor (\"olive\") in the right upper quadrant",
"       </td>",
"       <td>",
"        Pyloric stenosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Scrotum exam with tender swelling at the external ring, above and lateral to the pubis without an upper limit",
"       </td>",
"       <td>",
"        Incarcerated hernia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Apnea, bradycardia, temperature instability, bloody stools, abdominal distention",
"       </td>",
"       <td>",
"        Necrotizing enterocolitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Murmur, gallop, hepatosplenomegaly, edema, rales, grunting, flaring, retracting",
"       </td>",
"       <td>",
"        Congenital heart disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ambiguous genitalia in females, virilization in males",
"       </td>",
"       <td>",
"        Congenital adrenal hyperplasia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Jaundice, opisthotonus, high-pitched cry",
"       </td>",
"       <td>",
"        Acute bilirubin encephalopathy",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     UTI: urinary tract infection.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
