var title_f1_27_1468="Criteria multiple primary LC";
var content_f1_27_1468=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F61182&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F61182&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for diagnosis of multiple primary lung cancers",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Martini, N, Melamed, MR. Multiple primary cancers. J Thorac Cardiovasc Surg 1975; 70:606",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Definition of synchronous",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Concurrent, separate, malignant lung epithelial neoplasms with different histology, or",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tumors of similar histology arising in different locations (segments,",
"lobes, lung): (1) origin from carcinoma in situ, (2) no carcinoma in",
"lymphatics common to both, (3) no extrapulmonary metastases at the time",
"of diagnosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Definition of metachronous",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Histology different",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Histology same if: (1) free interval between cancers at least 2 years, or (2) origin from",
"carcinoma in situ, or (3) second cancer in different lobe or lung, and",
"(a) no carcinoma in lymphatics common to both, (b) no extrapulmonary",
"metastases at time of diagnosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Antakli, T, Schaefer, RF, Rutherford, JE, et al. Second primary lung cancer. Ann Thorac Surg 1995; 59:863",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Different histology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Same histology with two or more of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        1. Anatomically distinct",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        2. Associated premalignant lesion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        3. No systemic metastases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        4. No mediastinal spread",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        5. Different DNA ploidy",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
