var title_f1_27_1469="Urogynecologic reconstructive materials terminology";
var content_f1_27_1469=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F74128&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F74128&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    International Urogynecological Association/International Continence Society terminology and classification of complications related to insertion of prostheses and grafts in female pelvic floor surgery",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Terms used",
"       </td>",
"       <td class=\"subtitle1\">",
"        Definition",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other_start\">",
"        <strong>",
"         Prosthesis",
"        </strong>",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        A fabricated substitute to assist a damaged body part or to augment or stabilize a hypoplastic structure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mesh",
"       </td>",
"       <td>",
"        A (prosthetic) network fabric or structure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Implant",
"       </td>",
"       <td>",
"        A surgically inserted or embedded prosthesis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tape (sling)",
"       </td>",
"       <td>",
"        A flat strip of synthetic material",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"sublist_other_start\">",
"        <strong>",
"         Graft",
"        </strong>",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        Any tissue or organ for transplantation; refers to biological materials inserted",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Autologous grafts",
"       </td>",
"       <td>",
"        From the woman's own tissues (eg, dura mater, rectus sheath or fascia lata)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Allografts",
"       </td>",
"       <td>",
"        From post-mortem tissue banks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Xenografts",
"       </td>",
"       <td>",
"        From other species (eg, modified porcine dermis, porcine small intestine, bovine pericardium)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"sublist_other_start\">",
"        <strong>",
"         Complication",
"        </strong>",
"       </td>",
"       <td class=\"sublist_other_start\">",
"        A morbid process or event that occurs during the course of a surgery that is not an essential part of that surgery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Contraction",
"       </td>",
"       <td>",
"        Shrinkage or reduction in size",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Prominence",
"       </td>",
"       <td>",
"        Parts that protrude beyond the surface (eg, due to wrinkling or folding with no epithelial separation)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Separation",
"       </td>",
"       <td>",
"        Physically disconnected (eg, vaginal epithelium)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Exposure",
"       </td>",
"       <td>",
"        A condition of displaying, revealing, exhibiting or making accessible (eg, vaginal mesh visualized through separated vaginal epithelium)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Extrusion",
"       </td>",
"       <td>",
"        Passage gradually out of a body structure or tissue",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Compromise",
"       </td>",
"       <td>",
"        Bring into danger",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Perforation",
"       </td>",
"       <td>",
"        Abnormal opening in a hollow organ or viscus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Dehiscence",
"       </td>",
"       <td>",
"        A bursting open or gaping along natural or sutured line",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Haylen BT, Freeman RM, Swift SE, et al. An International Urogynecological Association (IUGA) / International Continence Society (ICS) joint terminology and classification of the complications related directly to the insertion of prostheses (meshes, implants, tapes) and grafts in female pelvic floor surgery. Int Urogynecol J 2011; 22:3, with kind permission from Springer Science + Business Media B.V. Copyright &copy; 2011.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
