var script_f1_30_1505=[" ",
"",
"var calctxt = '';",
"var xmltxt = '';",
"var htmtxt = '';",
"",
"",
"function DVTScoreWells_fx() {",
"with(document.DVTScoreWells_form){",
"",
"Score = 0.0;",
"doCalc = true;",
"",
"if (cc1.checked){",
"Score = Score + 1;",
"}",
"if (cc2.checked){",
"Score = Score + 1;",
"}",
"if (cc3.checked){",
"Score = Score + 1;",
"}",
"if (cc4.checked){",
"Score = Score + 1;",
"}",
"if (cc5.checked){",
"Score = Score + 1;",
"}",
"if (cc6.checked){",
"Score = Score + 1;",
"}",
"if (cc7.checked){",
"Score = Score + 1;",
"}",
"if (cc8.checked){",
"Score = Score + 1;",
"}",
"if (cc9.checked){",
"Score = Score + -2;",
"}",
"cctotal.value = Score;",
"",
"if (doCalc){",
"rrclr();",
"if ((Score >= 3) && (Score <= 8)){ document.getElementById('rr1_1').bgColor = '#cccccc';",
"}",
"if ((Score >= 1) && (Score <= 2)){ document.getElementById('rr1_2').bgColor = '#cccccc';",
"}",
"if ((Score >= -2) && (Score <= 0)){ document.getElementById('rr1_3').bgColor = '#cccccc';",
"}",
"",
"}",
"}",
"}",
"",
"",
"",
"",
"function rrclr(){",
"document.getElementById('rr1_1').bgColor = \"\";",
"document.getElementById('rr1_2').bgColor = \"\";",
"document.getElementById('rr1_3').bgColor = \"\";",
"}",
"",
"",
""].join("\n");
