" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   The molecular biology of melanoma",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?1/34/1576/contributors\">",
