var title_f1_39_1660="Potential toxicity herbs";
var content_f1_39_1660=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F65283&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F65283&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Cautions about herbs and dietary supplements when used in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Product",
"        <strong>",
"         purity",
"        </strong>",
"        is questionable; there is potential for misidentification and contamination with:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Pesticides",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Herbicides",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Pharmaceuticals",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Heavy metals",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Excipients such as alcohol",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Particularly for products imported from developing countries.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Reliability",
"        </strong>",
"        of products is uncertain; there is great variability in concentration of active ingredients within plants used, growing conditions, processing, and storage.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Effectiveness",
"        </strong>",
"        of active ingredients in children often not studied.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Safety and toxicity for short-term",
"        </strong>",
"        use in children is often unknown.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Safety and toxicity for chronic use",
"        </strong>",
"        in children is often unknown.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Unknown",
"        <strong>",
"         interactions",
"        </strong>",
"        with other medications and treatments.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Unknown",
"        <strong>",
"         metabolism",
"        </strong>",
"        in children with renal, hepatic, and other disorders.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Cost",
"        </strong>",
"        of products is not necessarily related to quality, purity, or concentration of active ingredients.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
