var title_f1_4_1100="CMML criteria WHO";
var content_f1_4_1100=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F55582&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F55582&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    WHO criteria for the diagnosis of chronic myelomonocytic leukemia (CMML)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Persistent peripheral blood monocytosis; absolute monocyte count &gt;1000/microL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Philadelphia chromosome or BCR/ABL fusion gene absent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &lt;20 percent myeloblasts + monoblasts + promonocytes in peripheral blood or bone marrow",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Dysplastic changes in one or more myeloid lineages. If myelodysplasia is absent or minimal, the diagnosis of CMML can be made if the above three criteria are met",
"        <strong>",
"         and:",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        An acquired clonal cytogenetic abnormality is present in bone marrow cells,",
"        <strong>",
"         or",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Persistent monocytosis for &ge;3 months and all other causes of monocytosis have been excluded",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         CMML-1:",
"        </strong>",
"        Blasts &lt;5 percent in peripheral blood and &lt;10 percent in bone marrow",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         CMML-2:",
"        </strong>",
"        Blasts 5 to 19 percent in peripheral blood or 10 to 19 percent in bone marrow,",
"        <strong>",
"         or",
"        </strong>",
"        Auer rods are present and blasts are &lt;20 percent in peripheral blood or bone marrow",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         CMML-1 or CMML-2 with eosinophilia:",
"        </strong>",
"        Above criteria for CMML-1",
"        <strong>",
"         or",
"        </strong>",
"        CMML-2 present along with peripheral blood absolute eosinophil count &gt;1500/ microL",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Vardiman JW, et al. Chronic myelomonocytic leukaemia. In: Jaffe ES, Harris NL, Stein H, Vardiman JW (Eds). World Health Organization Classification of Tumours. Pathology and Genetics of Tumours of Haematopoietic and Lymphoid Tissues, IARC Press, Lyon 2001. p.49. Permission granted from Harris NL and Vardiman JW.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
