var title_f1_42_1709="Pulmonary complications of allogeneic HCT - Preengraftment";
var content_f1_42_1709=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F83194&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F83194&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Pulmonary complications of allogeneic hematopoietic cell transplantation: Preengraftment",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Disease process",
"       </td>",
"       <td class=\"subtitle1\">",
"        Risk factors",
"       </td>",
"       <td class=\"subtitle1\">",
"        Associated manifestations",
"       </td>",
"       <td class=\"subtitle1\">",
"        Radiographic findings",
"       </td>",
"       <td class=\"subtitle1\">",
"        Useful diagnostic tests",
"       </td>",
"       <td class=\"subtitle1\">",
"        Lung biopsy needed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bacterial pneumonia",
"       </td>",
"       <td>",
"        Mucositis, neutropenia",
"       </td>",
"       <td>",
"        Fever, cough, sputum",
"       </td>",
"       <td>",
"        Usually focal consolidation; becomes diffuse in acute lung injury",
"       </td>",
"       <td>",
"        Broad based cultures; response to empiric antibiotics",
"       </td>",
"       <td>",
"        Generally not",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Fungal pneumonia",
"       </td>",
"       <td>",
"        Prolonged neutropenia; exposure to endemic fungi; prior treatment for invasive fungus",
"       </td>",
"       <td>",
"        Fever",
"       </td>",
"       <td>",
"        Focal nodular and consolidative opacities, \"halo sign\"",
"       </td>",
"       <td>",
"        Broad based cultures including BAL; &beta;-D-glucan; Aspergillus galactomannan; response to empiric antifungal therapy",
"       </td>",
"       <td>",
"        Sometimes",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Aspiration pneumonia",
"       </td>",
"       <td>",
"        Impaired swallowing due to mucositis; opiate therapy",
"       </td>",
"       <td>",
"        Fever, dyspnea",
"       </td>",
"       <td>",
"        Diffuse or focal ground glass or consolidative opacities",
"       </td>",
"       <td>",
"        Cultures are often negative",
"       </td>",
"       <td>",
"        No",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Permeability pulmonary edema",
"       </td>",
"       <td>",
"        Aspiration, engraftment syndrome, hyperacute GVHD, sepsis syndrome",
"       </td>",
"       <td>",
"        Fever, dyspnea",
"       </td>",
"       <td>",
"        Diffuse ground glass opacities",
"       </td>",
"       <td>",
"        Normal BNP, normal LV function on echocardiogram",
"       </td>",
"       <td>",
"        No",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Cardiogenic pulmonary edema",
"       </td>",
"       <td>",
"        Cardiotoxic medications; copious intravenous fluid administration",
"       </td>",
"       <td>",
"        Dyspnea, weight gain, peripheral edema",
"       </td>",
"       <td>",
"        Perihilar opacities in butterfly distribution, septal thickening, pleural effusion, cardiomegaly",
"       </td>",
"       <td>",
"        Elevated BNP; echocardiogram showing reduced LV function",
"       </td>",
"       <td>",
"        No",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Engraftment syndrome",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Erythematous maculo-papular rash, fever &gt;38.3, weight gain",
"       </td>",
"       <td>",
"        CT: bilateral ground-glass opacification, hilar or peribronchial consolidation, and thickening of interlobular septa",
"       </td>",
"       <td>",
"        Skin biopsy; BAL to exclude infection",
"       </td>",
"       <td>",
"        Sometimes to exclude other treatable causes, lung biopsy shows diffuse alveolar damage",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Hyperacute GVHD",
"       </td>",
"       <td>",
"        HLA mismatch",
"       </td>",
"       <td>",
"        Rash, abdominal cramps, diarrhea, elevated bilirubin",
"       </td>",
"       <td>",
"        Diffuse ground glass consistent with acute lung injury",
"       </td>",
"       <td>",
"        Skin biopsy; BAL to exclude infection",
"       </td>",
"       <td>",
"        Sometimes to exclude other processes",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     BAL: bronchoalveolar lavage; BNP: brain natriuretic protein; LV: left ventricular; CT: computed tomography; GVHD: graft-versus-host-disease; HLA: human leukocyte antigens; CTPA: computed tomography pulmonary angiogram.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
