var title_f1_44_1740="Addtl features possible MSA";
var content_f1_44_1740=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F68350&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F68350&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Additional features of possible MSA",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Possible MSA-P or MSA-C",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Babinski sign with hyperreflexia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Stridor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Possible MSA-P",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rapidly progressive parkinsonism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Poor response to levodopa",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Postural instability within 3 y of motor onset",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Gait ataxia, cerebellar dysarthria, limb ataxia, or cerebellar oculomotor dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Dysphagia within 5 y of motor onset",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Atrophy on MRI of putamen, middle cerebellar peduncle, pons, or cerebellum",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hypometabolism on FDG-PET in putamen, brainstem, or cerebellum",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Possible MSA-C",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Parkinsonism (bradykinesia and rigidity)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Atrophy on MRI of putamen, middle cerebellar peduncle, or pons",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hypometabolism on FDG-PET in putamen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Presynaptic nigrostriatal dopaminergic denervation on SPECT or PET",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     MSA: multiple system atrophy; MSA-P: MSA with predominant parkinsonism; MSA-C: MSA with predominant cerebellar ataxia; y: year; FDG: [",
"     <sup>",
"      18",
"     </sup>",
"     F]fluorodeoxyglucose.",
"    </div>",
"    <div class=\"reference\">",
"     Gilman, S, Wenning, GK, Low, PA, et al. Second consensus statement on the diagnosis of multiple system atrophy. Neurology 2008; 71:670.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
