var title_f1_44_1741="Specimen collection methods I";
var content_f1_44_1741=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F78605&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F78605&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Microbiology specimen handling and collection - I",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Specimen",
"       </td>",
"       <td class=\"subtitle1\">",
"        Method",
"       </td>",
"       <td class=\"subtitle1\">",
"        Volume",
"       </td>",
"       <td class=\"subtitle1\">",
"        Storage temperature*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"       <td class=\"subtitle1\">",
"        See UpToDate topic review",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"6\">",
"        Blood",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Routine",
"       </td>",
"       <td rowspan=\"2\">",
"        Blood culture vials",
"       </td>",
"       <td>",
"        Adults: 20-30 mL",
"       </td>",
"       <td rowspan=\"2\">",
"        25&deg;C",
"       </td>",
"       <td rowspan=\"2\">",
"        Obtain 2-3 sets",
"       </td>",
"       <td rowspan=\"2\">",
"        Technique of obtaining blood cultures for the detection of bacteremia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Children: 1-5 mL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Dimorphic fungi",
"       </td>",
"       <td rowspan=\"2\">",
"        Lysis centrifugation tube",
"       </td>",
"       <td rowspan=\"2\">",
"        10-20 mL",
"       </td>",
"       <td rowspan=\"2\">",
"        25&deg;C",
"       </td>",
"       <td>",
"        Lysis centrifugation tubes should be processed within eight hours s",
"       </td>",
"       <td rowspan=\"2\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Yeasts (eg, Candida) should be detectable using many modern routine media and blood culture systems",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Mycobacterial",
"       </td>",
"       <td>",
"        BacT/ALERT MB or Bactec MycoF/Lytic&nbsp;or lysis centrifugation tube",
"       </td>",
"       <td>",
"        10-20 mL",
"       </td>",
"       <td>",
"        25&deg;C",
"       </td>",
"       <td>",
"        Obtain one specimen",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         Catheter (vascular)",
"        </strong>",
"       </td>",
"       <td>",
"        Cleanse skin around insertion site with alcohol. Send catheter tip (5 cm) in sterile container",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        4&deg;C",
"       </td>",
"       <td>",
"        Also obtain two peripheral blood cultures",
"       </td>",
"       <td>",
"        Diagnosis and management of central venous catheter- related infections",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"5\">",
"        <strong>",
"         Cerebrospinal fluid",
"        </strong>",
"       </td>",
"       <td rowspan=\"5\">",
"        Sterile tube",
"       </td>",
"       <td class=\"sublist1_start\">",
"        At least 1-5 mL:",
"       </td>",
"       <td rowspan=\"5\">",
"        4&deg;C",
"       </td>",
"       <td rowspan=\"5\">",
"        Transport routine cultures at 25&deg;C, viral cultures at 4&deg;C (on ice). Also send extra fluid to store for possible future testing",
"       </td>",
"       <td rowspan=\"5\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Bacterial &ge;1 mL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Viral &ge;1 mL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        AFB &ge;2 mL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Fungal &ge;2 mL",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"6\">",
"        Other fluid",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Peritoneal, pericardial, pleural, synovial",
"       </td>",
"       <td>",
"        Sterile container or direct inoculation to blood culture vials",
"       </td>",
"       <td>",
"        At least 1-10 mL, but send as much fluid as possible",
"       </td>",
"       <td>",
"        4&deg;C",
"       </td>",
"       <td>",
"        Never send swab dipped in fluid",
"       </td>",
"       <td>",
"        Joint aspiration or injection: Technique and indications",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * If immediate processing is not possible.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
