var title_f1_45_1758="TNM staging thyroid cancer";
var content_f1_45_1758=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F73748&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F73748&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    TNM staging system for thyroid cancer",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"4\">",
"       Primary tumor (T)*",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       TX",
"      </td>",
"      <td colspan=\"3\">",
"       Primary tumor cannot be assessed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T0",
"      </td>",
"      <td colspan=\"3\">",
"       No evidence of primary tumor",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       T1",
"      </td>",
"      <td class=\"sublist_other_start\" colspan=\"3\">",
"       Tumor 2 cm or less in greatest dimension limited to the thyroid",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       T1a",
"      </td>",
"      <td class=\"sublist_other\" colspan=\"3\">",
"       Tumor 1 cm or less, limited to the thyroid",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       T1b",
"      </td>",
"      <td class=\"sublist_other\" colspan=\"3\">",
"       Tumor more than 1 cm but not more than 2 cm in greatest dimension, limited to the thyroid",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T2",
"      </td>",
"      <td colspan=\"3\">",
"       Tumor more than 2 cm but not more than 4 cm in greatest dimension limited to the thyroid",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T3",
"      </td>",
"      <td colspan=\"3\">",
"       Tumor more than 4 cm in greatest dimension limited to the thyroid or any tumor with minimal extrathyroid extension (eg, extension to sternothyroid muscle or perithyroid soft tissues)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       T4a",
"      </td>",
"      <td colspan=\"3\">",
"       Moderately advanced disease",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"3\">",
"       Tumor of any size extending beyond the thyroid capsule to invade subcutaneous soft tissues, larynx, trachea, esophagus, or recurrent laryngeal nerve",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       T4b",
"      </td>",
"      <td colspan=\"3\">",
"       Very advanced disease",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"3\">",
"       Tumor invades prevertebral fascia or encases carotid artery or mediastinal vessels",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"4\">",
"       All anaplastic carcinomas are considered T4 tumors",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       T4a",
"      </td>",
"      <td colspan=\"3\">",
"       Intrathyroidal anaplastic carcinoma",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       T4b",
"      </td>",
"      <td colspan=\"3\">",
"       Anaplastic carcinoma with gross extrathyroid extension",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"4\">",
"       Regional lymph nodes (N)&bull;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       NX",
"      </td>",
"      <td colspan=\"3\">",
"       Regional lymph nodes cannot be assessed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       N0",
"      </td>",
"      <td colspan=\"3\">",
"       No regional lymph node metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       N1",
"      </td>",
"      <td class=\"sublist_other_start\" colspan=\"3\">",
"       Regional lymph node metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       N1a",
"      </td>",
"      <td class=\"sublist_other\" colspan=\"3\">",
"       Metastasis to Level VI (pretracheal, paratracheal, and prelaryngeal/Delphian lymph nodes)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       N1b",
"      </td>",
"      <td class=\"sublist_other\" colspan=\"3\">",
"       Metastasis to unilateral, bilateral, or contralateral cervical (Levels I, II, III, IV, or V) or retropharyngeal or superior mediastinal lymph nodes (Level VII)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"4\">",
"       Distant metastasis (M)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       M0",
"      </td>",
"      <td colspan=\"3\">",
"       No distant metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       M1",
"      </td>",
"      <td colspan=\"3\">",
"       Distant metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"4\">",
"       Anatomic stage/prognostic groups &Delta;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"4\">",
"       Papillary or follicular (differentiated)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle3_left\" colspan=\"4\">",
"       Under 45 years",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Stage I",
"      </td>",
"      <td>",
"       Any T",
"      </td>",
"      <td>",
"       Any N",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Stage II",
"      </td>",
"      <td>",
"       Any T",
"      </td>",
"      <td>",
"       Any N",
"      </td>",
"      <td>",
"       M1",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle3_left\" colspan=\"4\">",
"       45 years and older",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Stage I",
"      </td>",
"      <td>",
"       T1",
"      </td>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Stage II",
"      </td>",
"      <td>",
"       T2",
"      </td>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\" rowspan=\"4\">",
"       Stage III",
"      </td>",
"      <td>",
"       T3",
"      </td>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T1",
"      </td>",
"      <td>",
"       N1a",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T2",
"      </td>",
"      <td>",
"       N1a",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T3",
"      </td>",
"      <td>",
"       N1a",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\" rowspan=\"6\">",
"       Stage IVA",
"      </td>",
"      <td>",
"       T4a",
"      </td>",
"      <td>",
"       N0",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T4a",
"      </td>",
"      <td>",
"       N1a",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T1",
"      </td>",
"      <td>",
"       N1b",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T2",
"      </td>",
"      <td>",
"       N1b",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T3",
"      </td>",
"      <td>",
"       N1b",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       T4a",
"      </td>",
"      <td>",
"       N1b",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Stage IVB",
"      </td>",
"      <td>",
"       T4b",
"      </td>",
"      <td>",
"       Any N",
"      </td>",
"      <td>",
"       M0",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Stage IVC",
"      </td>",
"      <td>",
"       Any T",
"      </td>",
"      <td>",
"       Any N",
"      </td>",
"      <td>",
"       M1",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Note: cTNM is the clinical classification, pTNM is the pathologic classification.",
"    <div class=\"footnotes\">",
"     * All categories may be subdivided: (s) solitary tumor and (m) multifocal tumor (the largest determines the classification).",
"     <br>",
"      &bull; Regional lymph nodes are the central compartment, lateral cervical, and upper mediastinal lymph nodes.",
"      <br>",
"       &Delta; Separate stage groupings are recommended for papillary or follicular (differentiated), medullary, and anaplastic (undifferentiated) carcinoma.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Used with the permission of the American Joint Committee on Cancer (AJCC), Chicago, Illinois. The original source for this material is the AJCC Cancer Staging Manual, Seventh Edition (2010) published by Springer New York, Inc.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
