var title_f1_48_1804="Confusion assessment method";
var content_f1_48_1804=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F69489&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F69489&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Confusion assessment method (CAM) for the diagnosis of delirium*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Feature",
"      </td>",
"      <td class=\"subtitle1\">",
"       Assessment",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       1. Acute onset and fluctuating course",
"      </td>",
"      <td>",
"       <p>",
"        Usually obtained from a family member or nurse and shown by positive responses to the following questions:",
"       </p>",
"       <p>",
"        \"Is there evidence of an acute change in mental status from the patient's baseline?\";",
"       </p>",
"       <p>",
"        \"Did the abnormal behavior fluctuate during the day, that is, tend to come and go, or increase and decrease in severity?\"",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       2. Inattention",
"      </td>",
"      <td>",
"       <p>",
"        Shown by a positive response to the following:",
"       </p>",
"       <p>",
"        \"Did the patient have difficulty focusing attention, for example, being easily distractible or having difficulty keeping track of what was being said?\"",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       3. Disorganized thinking",
"      </td>",
"      <td>",
"       <p>",
"        Shown by a positive response to the following:",
"       </p>",
"       <p>",
"        \"Was the patient's thinking disorganized or incoherent, such as rambling or irrelevant conversation, unclear or illogical flow of ideas, or unpredictable switching from subject to subject?\"",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\" rowspan=\"7\">",
"       4. Altered level of consciousness",
"      </td>",
"      <td>",
"       Shown by any answer other than \"alert\" to the following:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       \"Overall, how would you rate this patient's level ofconsciousness?\"",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Normal = alert",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Hyperalert = vigilant",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Drowsy, easily aroused = lethargic",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Difficult to arouse = stupor",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Unarousable = coma",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    *The diagnosis of delirium requires the presence of features 1 AND 2 plus either 3 OR 4.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
