"      <tr>",
"       <td class=\"indent1\">",
"        RSV prophylaxis (Palivizumab [Synagis])",
"       </td>",
"       <td>",
"        None",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Intravenous immune globulin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"2\">",
"        Replacement therapy (IVIG)",
"       </td>",
"      </tr>",
"      <tr>",
