"      <tr>",
"       <td class=\"sublist2\">",
"        1600-2000 mg/kg",
"       </td>",
"       <td class=\"sublist_other\">",
"        11",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cytomegalovirus immune globulin",
"       </td>",
"       <td>",
"        6",
"       </td>",
"      </tr>",
"      <tr>",
